import { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function ScrollToTop() {
  const history = useNavi();

  useEffect(() => {
    const unlisten = history.listen(() => {
      window.scrollTo(0, 0);
    });
    return () => {
      unlisten();
    };
  }, [history]);

  return null;
}

export default ScrollToTop;
